def statistics(loss, error, output, epoch, accuracy):
    print("")
    print("|------------------------------------------------------------------------|")
    print("|                                                                        |")
    print(f"   Loss - {loss:.8f}")
    print("|                                                                        |")
    print("|------------------------------------------------------------------------|")
    print("|                                                                        |")
    print("   Error - ", error[1])
    print("|                                                                        |")
    print("|------------------------------------------------------------------------|")
    print("|                                                                        |")
    print("   Output - ", output[1])
    print("|                                                                        |")
    print("|------------------------------------------------------------------------|")
    print("|                                                                        |")
    print("   Epoch - ", epoch)
    print("|                                                                        |")
    print("|------------------------------------------------------------------------|")
    print("|                                                                        |")
    print(f"   Accuracy - {accuracy}%")
    print("|                                                                        |")
    print("|------------------------------------------------------------------------|")
    print("")